package org.jboss.cdi.showcase.cart;import java.io.Serializable;import java.util.ArrayList;import java.util.List;import java.util.logging.Logger;import javax.enterprise.context.SessionScoped;import javax.inject.Named;@SuppressWarnings("serial")@SessionScoped@Named("cart")@Transactionalpublic class ShoppingCart implements Serializable {		private static final Logger log = Logger.getLogger(ShoppingCart.class.getName());		private List<Item> items = new ArrayList<Item>();		/**	 * Transactionally perform a checkout	 */	public List<Item> checkout() {		List<Item> ret = new ArrayList<Item>(items);		log.info("Removing items from cart");		items.clear();		log.info("Items removed from cart");		return ret;	}		public void add(Item i) {		items.add(i);	}}