package org.jboss.cdi.showcase.http;import javax.enterprise.inject.Produces;import javax.enterprise.inject.spi.InjectionPoint;import javax.inject.Inject;import javax.servlet.ServletRequest;import javax.servlet.http.HttpServletRequest;class HttpParams {		@Inject	HttpServletRequest request;	@Produces	@HttpParam("")	String getParamValue(InjectionPoint ip) {		System.out.println(request.getParameterNames());		return request.getParameter(ip.getAnnotated()				.getAnnotation(HttpParam.class).value());	}}