package org.jboss.cdi.showcase.translate;import java.util.Arrays;import java.util.Collections;import java.util.List;import java.util.regex.Pattern;/** * Parses a string into a list of sentences * */public class SentenceParser {		private static final String REGEX = "(?<=[.?!;])\\s+(?=\\p{Lu})";	private static final Pattern PATTERN = Pattern.compile(REGEX);		public List<String> parse(String text) {		if (text == null) {			return Collections.emptyList();		} else {			String [] s = PATTERN.split(text);			return Arrays.asList(s);		}	}	}